CREATE TABLE "squad" (
                          "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                          "name" varchar UNIQUE,
                          "max_size" int DEFAULT 5,
                          "cause" varchar,
                          "deleted" boolean DEFAULT false
);

CREATE TABLE "hero" (
                          "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                          "name" varchar UNIQUE,
                          "age" int,
                          "squad_id" int,
                          "strength_id" int,
                          "weakness_id" int,
                          "deleted" boolean DEFAULT false
);


CREATE TABLE "strength" (
                             "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                             "name" varchar UNIQUE,
                             "score" DOUBLE PRECISION,
                             "deleted" boolean DEFAULT false
);

CREATE TABLE "weakness" (
                            "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                            "name" varchar UNIQUE,
                            "score" DOUBLE PRECISION,
                            "deleted" boolean DEFAULT false
);


ALTER TABLE "hero" ADD FOREIGN KEY ("squad_id") references "squad" ("id");
ALTER TABLE "hero" ADD FOREIGN KEY ("strength_id") references "strength" ("id");
ALTER TABLE "hero" ADD FOREIGN KEY ("weakness_id") references "weakness" ("id");


